mapper.select:
SELECT BNO, TITLE, CONTENT, WRITER, REGDATE, UPDATEDATE, LIKECNT, READCNT, REPLYCNT, STAR, THUMB, CAMPNAME, CATEGORIES 
FROM (
	SELECT /*+ INDEX_DESC(TBL_REVIEW_BOARD PK_REVIEW_BOARD) */ ROWNUM rn, BNO, TITLE, CONTENT, WRITER, REGDATE, UPDATEDATE, LIKECNT, READCNT, REPLYCNT, STAR, THUMB, CAMPNAME, CATEGORIES 
	FROM TBL_REVIEW_BOARD
	WHERE 

		ROWNUM <= #{pageNum} * #{amount}






		) 

		WHERE rn > (#{pageNum} - 1) * #{amount}

SELECT BNO, TITLE, CONTENT, WRITER, TO_CHAR(REGDATE, 'YYYY-MM-DD') as REGDATE, TO_CHAR(UPDATEDATE, 'YYYY-MM-DD') as UPDATEDATE, LIKECNT, READCNT, REPLYCNT, READCNT, ZIPCODE, ADDRESS, ADDRESS_DETAIL, ADDRESS_ETC, STAR, THUMB, CAMPNAME
FROM TBL_REVIEW_BOARD
WHERE BNO = #{bno}

mapper.update:
UPDATE TBL_REVIEW_BOARD
SET TITLE = #{title}, CONTENT = #{content}, UPDATEDATE = SYSDATE, ZIPCODE = #{zipcode}, ADDRESS = #{address}, ADDRESS_DETAIL = #{addressDetail}, ADDRESS_ETC = #{addressEtc}, STAR = #{star}, THUMB = #{thumb}, CAMPNAME = #{campName} 
WHERE BNO = #{bno}

UPDATE TBL_REVIEW_BOARD
SET READCNT = READCNT + 1
WHERE BNO = #{bno}

UPDATE TBL_REVIEW_BOARD
SET REPLYCNT = REPLYCNT + #{amount}
WHERE BNO = #{bno}

UPDATE TBL_REVIEW_BOARD
SET LIKECNT = LIKECNT + #{amount}
WHERE BNO = #{bno}

mapper.select:
SELECT COUNT(BNO) 
FROM TBL_REVIEW_LIKE 
WHERE BNO = #{bno} AND USERID = #{userId}

mapper.update:
UPDATE TBL_REVIEW_BOARD 
SET LIKECNT = LIKECNT + 1 
WHERE BNO = #{bno}

UPDATE TBL_REVIEW_BOARD 
SET LIKECNT = LIKECNT - 1 
WHERE BNO = #{bno}
